```typescript
async () => {
  const firstName = TEST_DATA_GENERATOR.firstName();
  const lastName = TEST_DATA_GENERATOR.lastName();
  const mi = TEST_DATA_GENERATOR.firstName().charAt(0);
  const newTemplateProps = {
    name: `${TEST_DATA_GENERATOR.word()} ${TEST_DATA_GENERATOR.word()}`,
    data: {
      owner: {
        firstName,
        lastName,
        mi,
        name: `${firstName} ${lastName}`,
        ownerEmail: TEST_DATA_GENERATOR.email(),
        cellPhone: TEST_DATA_GENERATOR.phoneNumber(),
        type: "Person",
        mvlConnected: false,
        id: null,
      },
      additionalEmail: TEST_DATA_GENERATOR.email(),
      feedMillEmail: TEST_DATA_GENERATOR.email(),
      headCount: 1,
      species: "Bovine",
      drug_id: global.TEST_DATA.DRUGS.VFD.id,
    },
    version: 1,
  };
  const response = await CRICKET_API.callApi({
    endpoint: `templates/${TEMPLATE.id}`,
    method: "PUT",
    data: newTemplateProps,
  });

  expect(response).toHaveStatusCode(200);
  expect(response.duration).toHaveResponseTimeBelow(10);
  expect(response.data).toMatchObject({
    ...TEMPLATE,
    ...newTemplateProps,
    lastUpdated: expect.any(String),
  });
  expect(response.data).toMatchSchema("response");
};

