```typescript
async () => {
  const type = "clinic";
  const association = "CVMA";
  const lead = await CRICKET_API.callApi({
    endpoint: "lead",
    method: "POST",
    data: {
      ...LEAD,
      type,
      association,
      associationNumber: TEST_DATA_GENERATOR.number().toString(),
      complete: true,
    },
  }).then((r) => r.data);
  const response = await CRICKET_API.callApi({
    endpoint: "lead/additionalInfoSave",
    method: "POST",
    data: {
      leadId: lead.id.toString(),
      speciesList: [TEST_DATA_GENERATOR.species()],
      vetCount: TEST_DATA_GENERATOR.number(),
      eiaCount: TEST_DATA_GENERATOR.number(),
      cviCount: TEST_DATA_GENERATOR.number(),
      vfdCount: TEST_DATA_GENERATOR.number(),
      scriptCount: TEST_DATA_GENERATOR.number(),
      preferredLab: `${TEST_DATA_GENERATOR.companyName()} Lab`,
      additionalInfo: TEST_DATA_GENERATOR.sentence(),
    },
  });

  expect(response).toHaveStatusCode(200);
  expect(response.duration).toHaveResponseTimeBelow(10);
  expect(response.data).toStrictEqual(lead);
  expect(response.data).toMatchSchema("response");
};

