### Test
```typescript
async () => {
  const response = await CRICKET_API.callApi({
    endpoint: "origin/potential",
    method: "POST",
    data: {
      type: "email",
      data: {
        email: ORIGIN_PERSON.email,
      },
    },
  });

  expect(response).toHaveStatusCode(200);
  expect(response.duration).toHaveResponseTimeBelow(10);
  expect(response.data).toStrictEqual(expect.any(Array));
  expect(response.data.length).toBeGreaterThanOrEqual(1);
  expect(response.data).toStrictEqual(
    expect.arrayContaining([
      expect.objectContaining({
        id: ORIGIN_PERSON.id,
        label: ORIGIN_PERSON.label,
        docCount: 0,
        mvlConnected: ORIGIN_PERSON.mvlConnected,
      }),
    ])
  );
  expect(response.data).toMatchSchema("response");
};

